    <script>
        const vueApp = new Vue({
            delimiters: ['{(', ')}'],
            el: '#login',
            data() {
                return {
                    lead: {
                        lead_id: 0,
                        phone_consent: '',
                        agent_id: null,
                        first_name: null,
                        last_name: null,
                        email: null,
                        email_optin: 1,
                        phone_on: 1,
                        text_on: 1,
                        phone: null,
                        lender_id: null,
                        signup_mlsid: '',
                        signup_mls: '',
                        town: '',
                        price:'0',
                        lang: 'en',
                        routing_note: '',
                        routing_tag: '',
                    },
                    formEnabled: true
                }
            },
            methods: {
                async saveLead() {
                    try {
                        this.formEnabled = false
                        this.lead.routing_tag = $(this.$el).find('#routing-tag').val();
                        this.lead.routing_note = $(this.$el).find('#routing-note').val();
                        
                        const response = await fetch('/ajax/api.php?add_lead=1&ftype=login', {
                            method: 'POST',
                            headers: {
                                'Content-Type': 'application/json',
                                Accept: 'application/json',
                            },
                            body: JSON.stringify(this.lead),
                        });
                        if (response.status === 200) {
                            if(window.IREGATracking) {
                                window.IRESendEvent?.('registration.email')
                            }
                            const jsonResponse = await response.json();
                            this.setLead(jsonResponse.lead)
    
                            if (window.saveSearchAfterLogin) {
                                window.saveSearchAfterLogin = false;
                                $('#search-alert-modal').modal('toggle');
                            }
    
                            return jsonResponse
                        }
    
                    } catch (error) {
                        console.log(error);
                    }
                },
                setLead(lead) {
    
                    Object.assign(this.lead, lead);
    
                    if (this.lead.lead_id) {
    
                        document.body.dataset.is_logged_in = 1
    
                        const showingEmail = document.getElementById('request-showing-email');
                        if (showingEmail) {
                            showingEmail.value = this.lead.email;
                        }
    
                        const showingPhone = document.getElementById('request-showing-phone');
                        if (showingPhone) {
                            showingPhone.value = this.lead.phone;
                        }
    
                        const qButtons = document.querySelectorAll('.ask-question')
                        if (qButtons) {
                            qButtons.forEach((el) => {
                                el.dataset.is_logged_in = 1
                            })
                        }
    
                        const questionEmail = document.getElementById('ask-question-email')
                        if(questionEmail) {
                            questionEmail.value = this.lead.email
                            questionEmail.disabled = true;
                        }
    
                        const sButtons = document.querySelectorAll('.saveListing')
                        if (sButtons) {
                            sButtons.forEach((el) => {
                                el.dataset.leadid = this.lead.lead_id
                            })
                        }
    
                        const shwButtons = document.querySelectorAll('.showing-request')
                        if (shwButtons) {
                            shwButtons.forEach((el) => {
                                el.dataset.is_logged_in = 1
                            })
                        }
    
                        const eAlertsButton = document.getElementById('email_alerts_login')
                        if (eAlertsButton) {
                            eAlertsButton.parentElement.remove();
                        }
    
                        const loginModal = document.getElementById('login_modal')
                        if (loginModal) {
                            loginModal.dataset.force_lead_registration = 0
                            loginModal.dataset.prompt_registration = 0
                        }
    
                        const gallery = document.querySelector('.gallery')
                        if (gallery) {
                            gallery.dataset.force_lead_registration = 0
                        }
                        
                        const loginItems = document.querySelectorAll('li.login');
                        if(loginItems) {
                            loginItems.forEach((item) => {
                                item.innerHTML = `<a aria-label="logout" href="/login.php">
                                <i class="fa fa-user" aria-hidden="true"></i> 
                                <span class="hidden-sm-down">${this.lead.first_name}</span>
                                </a>`
                            })
                        }
    
                        const listingSearchForm = document.getElementById('listing_search_form');
                        if (listingSearchForm) {
                            const input = document.createElement('input');
                            input.type = 'hidden';
                            input.name = 'leadid';
                            input.value = this.lead.lead_id;
                            listingSearchForm.appendChild(input);
                        }
                        
                        $(loginModal).modal('toggle')
                        this.formEnabled = true
                    }
                },
            },
            watch: {
                formEnabled(n, o) {
                    const form = this.$refs.regForm
                    const elements = form.elements;
                    const button = this.$refs.regButton
    
                    for (let i = 0, len = elements.length; i < len; ++i) {
                        elements[i].readOnly = !n;
                    }
    
                    button.innerHTML = n ? 'Continue With Email' :
                            'Sending <i class="fa fa-refresh fa-spin"></i>';
                },
                'lead.phone': {
                        handler(n, o) {
                            if (this.$refs.regButton.disabled) {
                                if (/\d{3}-\d{3}-\d{4}/.test(n)) {
                                    this.$refs.regButton.disabled = false
                                } else {
                                    this.$refs.regButton.disabled = true
                                }
                            }
                        },
                        deep: true,
                },
            },
        })
    
    </script>
    
    
    
    
    
    
    
    
